import React, { useState, useEffect } from 'react';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Sample data - replace image paths with your actual images
  const slides = [
    {
      image: 'https://images.unsplash.com/photo-1602143407151-7111542de6e8',
      title: 'Modern Glass Collection',
      description: 'Elegant and sustainable glass water bottles'
    },
    {
      image: 'https://images.unsplash.com/photo-1523362628745-0c100150b504',
      title: 'Premium Sports Series',
      description: 'High-performance bottles for active lifestyles'
    },
    {
      image: 'https://images.unsplash.com/photo-1536939459926-301728717817',
      title: 'Eco-Friendly Range',
      description: 'Sustainable choices for a better planet'
    },
    {
      image: 'https://images.unsplash.com/photo-1581783898377-1c85bf937427',
      title: 'Designer Collection',
      description: 'Uniquely crafted bottles for the style-conscious'
    },
    {
      image: 'https://images.unsplash.com/photo-1589365278144-c9e705f843ba',
      title: 'Minimalist Series',
      description: 'Clean, simple designs for modern living'
    },
    {
      image: 'https://images.unsplash.com/photo-1556767576-5ec41e3239ea',
      title: 'Active Lifestyle Range',
      description: 'Durable bottles for your daily adventures'
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
    }, 5000); // Change slide every 5 seconds

    return () => clearInterval(timer);
  }, []);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + slides.length) % slides.length);
  };

  return (
    <div className="relative h-[calc(100vh-5rem)] overflow-hidden">
      {/* Carousel */}
      <div className="absolute inset-0">
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${
              currentSlide === index ? 'opacity-100' : 'opacity-0'
            }`}
          >
            {/* Gradient Overlay */}
            <div className="absolute inset-0 bg-gradient-to-r from-black/70 to-transparent z-10" />
            
            {/* Background Image */}
            <img
              src={slide.image}
              alt={slide.title}
              className="w-full h-full object-cover object-center"
            />
          </div>
        ))}
      </div>

      {/* Content */}
      <div className="relative z-20 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 h-full">
        <div className="flex flex-col justify-center h-full">
          <div className="max-w-xl space-y-8">
            {/* Animated Title */}
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-accent font-bold text-white opacity-0 animate-fadeIn">
              {slides[currentSlide].title}
            </h1>
            
            {/* Animated Description */}
            <p className="text-lg md:text-xl text-gray-200 font-body opacity-0 animate-fadeInDelay">
              {slides[currentSlide].description}
            </p>

            {/* CTA Buttons */}
            <div className="flex space-x-4 opacity-0 animate-fadeInDelay2">
              <button className="px-8 py-3 bg-lexa-600 text-white rounded-full hover:bg-lexa-700 transition duration-300 font-heading group relative overflow-hidden">
                <span className="relative z-10">Shop Now</span>
                <div className="absolute inset-0 bg-white/10 transform -translate-x-full group-hover:translate-x-0 transition-transform duration-500 ease-out" />
              </button>
              <button className="px-8 py-3 border-2 border-white text-white rounded-full hover:bg-white/10 transition duration-300 font-heading">
                Learn More
              </button>
            </div>
          </div>
        </div>

        {/* Navigation Arrows */}
        <button
          onClick={prevSlide}
          className="absolute left-4 top-1/2 -translate-y-1/2 p-2 rounded-full bg-black/30 text-white hover:bg-black/50 transition-colors"
          aria-label="Previous slide"
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>
        <button
          onClick={nextSlide}
          className="absolute right-4 top-1/2 -translate-y-1/2 p-2 rounded-full bg-black/30 text-white hover:bg-black/50 transition-colors"
          aria-label="Next slide"
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
        </button>

        {/* Slide Indicators */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-3">
          {slides.map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentSlide(index)}
              className={`w-3 h-3 rounded-full transition-all duration-300 ${
                currentSlide === index
                  ? 'bg-white w-8'
                  : 'bg-white/50 hover:bg-white/75'
              }`}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Hero;
